mapper.update:
UPDATE BOARD
SET HIT = HIT +1
WHERE BSEQ = #{bseq}

UPDATE BOARD
SET TITLE = #{title}, CONTENT = #{content}, REGDATE = SYSDATE
WHERE BSEQ = #{bseq}

mapper.select:
SELECT * FROM(SELECT ROWNUM RNUM, S.* FROM (SELECT B.BSEQ, B.BCODE, B.ID, B.TITLE, B.CONTENT , B.IMG, B.HIT, B.REGDATE, B.NOTICE, M.NAME FROM BOARD B JOIN MEMBER M ON B.ID = M.ID WHERE B.BCODE = #{bcode}
AND M.NAME = #{word}
AND B.TITLE LIKE '%'||#{word}||'%'
ORDER BY B.REGDATE DESC) S) B WHERE B.RNUM BETWEEN #{searchPageNum}*10-9 AND #{searchPageNum}*10

SELECT COUNT(*) FROM (SELECT B.BSEQ, B.BCODE, B.ID, B.TITLE, B.CONTENT , B.IMG, B.HIT, B.REGDATE, B.NOTICE, M.NAME FROM BOARD B JOIN MEMBER M ON B.ID = M.ID WHERE B.BCODE = #{bcode}
AND M.NAME = #{word}
AND B.TITLE LIKE '%'||#{word}||'%'
ORDER BY B.REGDATE DESC)

SELECT COUNT(*)
FROM BOARD
WHERE BCODE = #{bcode}

SELECT COUNT(*)
FROM MEMO
WHERE BSEQ = #{bseq};
