mapper.select:
SELECT NVL(count(BSEQ),0) cnt 
FROM board 
WHERE id=#{id}

SELECT NVL(count(MSEQ),0) cnt 
FROM memo 
WHERE id=#{id}

mapper.update:
UPDATE member
SET id = #{id}
	, pwd = #{pwd}
	, name = #{name}
	, gender = #{gender}
	, birth = #{birth}
	, phone = #{phone}
WHERE id = #{id}
